mapper.select:
SELECT 
  NAME
                       
    ,SUM(if(SUBSTR(OFFER_DTM,1,6) = '${date}', OFFER_VAL, NULL)) AS '${date}'
    
    ,SUM(if(SUBSTR(OFFER_DTM,1,6) BETWEEN #{startYM} AND #{endYM}, OFFER_VAL, NULL)) AS 'TOTAL'     
 FROM OFFER
 WHERE OFFER_TYP IN (
 
    '${cd}'
     
 AND CH_NO = #{ch_no}   
 )         
GROUP BY NAME;
